import React, { Component } from 'react';
import axios from 'axios';
import "./Register.css"

class RegistrationForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      deceasedName: '',
      dateOfBirth: '',
      dateOfPassing: '',
      briefDescription: '',
      deathCertificateNumber: '',
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission, e.g., send data to the server or perform validation.
    const registrationData = {
      deceasedName: this.state.deceasedName,
      dateOfBirth: this.state.dateOfBirth,
      dateOfPassing: this.state.dateOfPassing,
      briefDescription: this.state.briefDescription,
      deathCertificateNumber: this.state.deathCertificateNumber,
    }

    // You can use Axios or fetch to send data to your Express backend.
// Make a POST request to the server API
axios.post('/api/register', registrationData)
.then((response) => {
  // Registration was successful
  console.log(response.data.message);
  // You can handle success, e.g., redirect to a thank you page
})
.catch((error) => {
  // Handle registration error
  console.error('Error registering deceased:', error);
  // You can display an error message to the user
});

  }

  render() {
    return (
      <div className="registration-container">
        <h2>Register a Death</h2>
        <form onSubmit={this.handleSubmit}>
          <div className="input-group">
            <label htmlFor="deceasedName">Deceased Name</label>
            <input
              type="text"
              id="deceasedName"
              name="deceasedName"
              value={this.state.deceasedName}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="dateOfBirth">Date of Birth</label>
            <input
              type="date"
              id="dateOfBirth"
              name="dateOfBirth"
              value={this.state.dateOfBirth}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="dateOfPassing">Date of Passing</label>
            <input
              type="date"
              id="dateOfPassing"
              name="dateOfPassing"
              value={this.state.dateOfPassing}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="briefDescription">Brief Description</label>
            <textarea
              id="briefDescription"
              name="briefDescription"
              value={this.state.briefDescription}
              onChange={this.handleInputChange}
              rows="4"
              required
            ></textarea>
          </div>
          <div className="input-group">
            <label htmlFor="deathCertificateNumber">Death Certificate Number</label>
            <input
              type="text"
              id="deathCertificateNumber"
              name="deathCertificateNumber"
              value={this.state.deathCertificateNumber}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <button type="submit" onClick={handleSubmit}>Register</button>
        </form>
      </div>
    );
  }
}

export default RegistrationForm;
