import React, { Component } from 'react';
import "./Dashboard.css";

class ProfileManagerDashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      profileManagerName: '',
      relationshipToDeceased: '',
      isApproved: false,
      // Add other necessary state variables here
    };
  }

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  }

  handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission, e.g., update profile manager details.
    // You can use Axios or fetch to send data to your server.
    // Set the "isApproved" state to true after 48 hours of approval.
  }

  render() {
    return (
      <div className="profile-manager-dashboard">
        <h2>Profile Manager Dashboard</h2>
        <form onSubmit={this.handleSubmit}>
          <div className="input-group">
            <label htmlFor="profileManagerName">Your Name</label>
            <input
              type="text"
              id="profileManagerName"
              name="profileManagerName"
              value={this.state.profileManagerName}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="relationshipToDeceased">Relationship to Deceased</label>
            <input
              type="text"
              id="relationshipToDeceased"
              name="relationshipToDeceased"
              value={this.state.relationshipToDeceased}
              onChange={this.handleInputChange}
              required
            />
          </div>
          {/* Add other form fields for additional functionalities here */}
          <button type="submit">Update Profile</button>
        </form>
        {this.state.isApproved ? (
          <p>Your profile update will be reviewed and approved within 48 hours. Thank you!</p>
        ) : null}
      </div>
    );
  }
}

export default ProfileManagerDashboard;
